import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';
import { MatSnackBar } from '@angular/material/snack-bar';
import { LoginService } from '../login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  public username: any;
  public password: any;
  public loginusername:any;
  public name: string | undefined;

  constructor(
    private http: HttpClient,
    private router: Router,
    private snackBar: MatSnackBar,
    private LoginService:LoginService
  ) {}

  ngOnInit(): void {
    this.username = '';
    this.password = '';
  }

  login(): void {
    const httpOptions = {
      headers: new HttpHeaders({ 'Content-Type': 'application/json' }),
    };

    this.http
      .post(
        'http://127.0.0.1:8080/login',
        JSON.stringify({ username: this.username, password: this.password }),
        httpOptions
      )
      .subscribe(
        (response) => {
          console.log(response);
          this.router.navigate(['/viewprofile', { username: this.username }]);
          this.loginauth()
        },
        (error) => {
          this.snackBar.open(error.error.message, 'Dismiss', {
            duration: 5000,
          });
        }
      );
  }
  signup() {
    const httpOptions = {
      headers: new HttpHeaders({ 'Content-Type': 'application/json' }),
    };

    this.http
      .post(
        'http://127.0.0.1:8080/add_user',
        JSON.stringify({ username: this.username, password: this.password}),
        httpOptions
      )
      .subscribe(
        (response) => {
          console.log(response);
        },
        (error) => {
          console.log(error);
        }
      );
    this.router.navigate(['/signup']);
  }
  loginauth() {
    this.LoginService.login(this.username);
    this.router.navigate(['/viewprofile', { username: this.username }]);
    }

    logout() {
    this.LoginService.logout();
    this.router.navigate(['/login']);
    }
    // openSignupForm() {
    //   const dialogRef = this.dialog.open(SignupComponent, {
    //     width: '250px',
    //     data: { username: this.username, password: this.password}
    //   });
    //   dialogRef.afterClosed().subscribe(result => {
    //   console.log('The dialog was closed');
    //   });
    // }

}
<div class="mx-auto ">
  <div class="login-container container-fluid h-">
    <div class="container">
      <div class="d-flex justify-content-center logo-container h-50">
        <img src="assets/logo.png" alt="Logo" style="max-width: auto; height: auto; margin: 0 auto;">
      </div>
    </div>
    <div class="d-flex justify-content-center text-align-center py-1 mx-auto my-auto">
      <h1 class="text-center">Welcome to MY University Social Network</h1>
    </div>
  </div>
  <div class="container-fluid py-4">
    <div class="d-flex justify-content-center py-4">
      <div class="col-md-4 col-sm-12 col-xs-12 py-4">
        <form>
          <div class="form-group">
            <label for="username" class="form-label" style="color:#ED3237">Username</label>
            <input type="text" style="background-color: ghostwhite; color:black" id="username" [(ngModel)]="username"
              name="username" class="form-control">
          </div>
          <div class="form-group">
            <label class="form-label" for="password" style="color:#ED3237">Password</label>
            <input type="password" style="background-color: ghostwhite; color:black" id="password"
              [(ngModel)]="password" name="password" class="form-control">
          </div>
          <div class="form-group d-flex justify-content-center">
            <button (click)="login()" class="login-button">Login</button>
            <button (click)="signup()">Sign Up</button>
          </div>
        </form>
      </div>
    </div>
    <p class="text-center" style="font-size: large;">Made By Usman Javed Swati (AAI 212 003) & Ahsan Shah (ADC 212 013) </p>
  </div>
</div>
<div class="modal" id="signupModal">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h4 class="modal-title">Sign Up</h4>
        <button type="button" class="close" data-dismiss="modal">&times;</button>
      </div>
      <div class="modal-body">
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>
